mapper.select:
SELECT * FROM user
WHERE status = 'active'



        AND email = #{account}


        AND phone = #{account}


        AND username = #{account}


AND password = #{password}
LIMIT 1

SELECT * FROM user
WHERE 1=1


    AND (
    username LIKE CONCAT('%', #{keyword}, '%')
    OR nickname LIKE CONCAT('%', #{keyword}, '%')
    OR email LIKE CONCAT('%', #{keyword}, '%')
    )




    AND status IN

        #{status}





    AND role IN

        #{role}





    AND create_time >= #{startDate}


    AND create_time <= #{endDate}



ORDER BY create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM user
WHERE 1=1

    AND (
    username LIKE CONCAT('%', #{keyword}, '%')
    OR nickname LIKE CONCAT('%', #{keyword}, '%')
    OR email LIKE CONCAT('%', #{keyword}, '%')
    )


    AND status IN

        #{status}



    AND role IN

        #{role}



    AND create_time >= #{startDate}


    AND create_time <= #{endDate}

SELECT * FROM user
WHERE 1=1

    AND username = #{username}


    AND email = #{email}


    AND role = #{role}


    AND login_count >= #{minLoginCount}


    AND login_count <= #{maxLoginCount}




    AND (username LIKE #{pattern} OR nickname LIKE #{pattern})

mapper.insert:
INSERT INTO user
username, password, email, gender, phone, resume, role, status, login_count, create_time, update_time
#{username}, #{password}, #{email}, #{gender}, #{phone}, #{resume}, #{role}, #{status}, #{loginCount}, #{createTime}, #{updateTime}

mapper.update:
UPDATE user
username = #{username}, password = #{password}, email = #{email}, nickname = #{nickname}, phone = #{phone}, avatar = #{avatar}, role = #{role}, status = #{status}, login_count = #{loginCount}, last_login_time = #{lastLoginTime}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT * FROM user
WHERE status != 'deleted'

    AND (username LIKE CONCAT('%', #{search}, '%')
    OR email LIKE CONCAT('%', #{search}, '%')
    OR phone LIKE CONCAT('%', #{search}, '%'))


    AND status = #{status}


    AND role = #{role}

ORDER BY create_time DESC

    LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM user WHERE status != 'deleted'
AND (username LIKE CONCAT('%', #{search}, '%') OR email LIKE CONCAT('%', #{search}, '%') OR phone LIKE CONCAT('%', #{search}, '%'))
AND status = #{status}
AND role = #{role}

SELECT COUNT(*) FROM user
WHERE username = #{username}

    AND id != #{excludeUserId}

AND status != 'deleted'

SELECT COUNT(*) FROM user
WHERE email = #{email}

    AND id != #{excludeUserId}

AND status != 'deleted'

SELECT COUNT(*) FROM user
WHERE phone = #{phone}

    AND id != #{excludeUserId}

AND status != 'deleted'

SELECT * FROM user
WHERE status = #{status}
ORDER BY create_time DESC

